import axios from 'axios';
import { createAsyncThunk } from '@reduxjs/toolkit';

axios.defaults.baseUrl = "https://6372740e025414c637102dd0.mockapi.io/contacts";

export const fetchContacts = createAsyncThunk("/fetchAll", async (_, thunkApi) => {
    try {
        const response = await axios.get("/");
        return response.data;
    } catch (error) {
        return thunkApi.rejectWithValue(error.massage);
    };
});
export const deleteContact = createAsyncThunk("/deleteContact-", async (id, thunkApi) => {
    try {
        const response = await axios.delete(`/${id}`);
        return response.data;
    } catch (error) {
        return thunkApi.rejectWithValue(error.massage);
    };
});
export const addContact = createAsyncThunk("/addcontact", async (contactObj, thunkApi) => {
    try {
        const response = await axios.post("/", contactObj);
        return response.data;
    } catch (error) {
        return thunkApi.rejectWithValue(error.massage);
    };
});
