import axios from "axios";
import { createAsyncThunk } from "@reduxjs/toolkit";


axios.defaults.baseURL = "https://637369250bb6b698b60b3203.mockapi.io/contacts";

export const fetchContacts = createAsyncThunk("contact/fetchAll", async (_, thunkAPI) => {
  try {
    const response = await axios.get("/contact");
    return response.data;
  } catch (error) {
    return thunkAPI.rejectWithValue(error.message)
  }
});

export const deleteContact = createAsyncThunk("contact/deleteContact", async (id, thunkAPI) => {
  try {
    const response = await axios.delete(`/contact/${id}`);
    return response.data;
  } catch (error) {
    return thunkAPI.rejectWithValue(error.message)
  }
});

export const addContact = createAsyncThunk("contact/addContact", async (contactObj, thunkAPI) => {
  try {
    const response = await axios.post('/contact', contactObj);
    return response.data;
  } catch (error) {
    return thunkAPI.rejectWithValue(error.message)
  }
});
