import axios from "axios";
import { createAsyncThunk } from "@reduxjs/toolkit";

axios.defaults.baseURL = "https://637360fc0bb6b698b60a4837.mockapi.io";

export const fetchContacts = createAsyncThunk("api/fetchAll", async (_, thunkAPI) => {
  try {
    const response = await axios.get("/api");
    return response.data;
  } catch (error) {
    return thunkAPI.rejectWithValue(error.message)
  }
});

export const deleteContact = createAsyncThunk("api/deleteContact", async (id, thunkAPI) => {
  try {
    const response = await axios.delete(`/api/${id}`);
    return response.data;
  } catch (error) {
    return thunkAPI.rejectWithValue(error.message)
  }
});

export const addContact = createAsyncThunk("api/addContact", async (contactObj, thunkAPI) => {
  try {
    const response = await axios.post('/api', contactObj);
    return response.data;
  } catch (error) {
    return thunkAPI.rejectWithValue(error.message)
  }
});
